sys_variable_value.value:
(function execute(inputs, outputs) {

  var reverseDomainName = '';
  var reverseZoneName = ''; 

  var type = inputs.type.toString().toUpperCase();
  var ipAddress = inputs.answer.toString();

  if (type == 'A'){

    reverseDomainName = ipAddress.split('.').reverse().join('.')+".in-addr.arpa";
    reverseZoneName = reverseDomainName.substring(reverseDomainName.indexOf(".") + 1);

  }else if (type == 'AAAA'){

    var arr = ipAddress.split(':');
    var reverseArr = arr.reverse();

    for(var i in reverseArr){

      if(reverseArr[i].length !=0 ){
        reverseDomainName += reverseArr[i].toString().split('').reverse().join('.') +'.';

        if(i != 0)
          reverseZoneName += reverseArr[i].toString().split('').reverse().join('.')+'.';		
      }
    }

    reverseDomainName += "ip6.arpa";
    reverseZoneName += "ip6.arpa";
  }

  outputs.reversedomainname = reverseDomainName;
  outputs.reversezonename = reverseZoneName;


})(inputs, outputs);

